import * as React from 'react';
import { useRouter } from 'next/router';
import { Box, Flex, PseudoBox } from '@chakra-ui/core';
import { Logo } from '../Logo';
import { Button } from '../Button';
import { Link } from '../Link';
import { useConfig, useTheme, useColorMode } from '../../context';

const bg = { dark: 'original.dark', light: 'transparent' };
const accent = { dark: 'original.tertiary', light: 'original.primary' };
const variant = { dark: 'tertiary', light: 'primary' };
const text = { dark: 'light.200', light: 'white' };
const textHover = { dark: 'light.50', light: 'dark.600' };

const Header = props => {
  const { colorMode } = useColorMode();
  return (
    <Box
      as="header"
      pos="fixed"
      height={32}
      top={0}
      left={0}
      right={0}
      w="100%"
      zIndex={1000}
      bg={bg[colorMode]}
      {...props}
    />
  );
};

const Navbar = props => {
  return (
    <Flex
      as="nav"
      pos="relative"
      px={24}
      py={8}
      alignItems="center"
      flexDir="row"
      flexWrap="nowrap"
      justifyContent="flex-start"
      {...props}
    />
  );
};

const ItemGroup = props => (
  <Flex
    flex="1 0 0"
    alignItems="center"
    pointerEvents="auto"
    justifyContent="flex-end"
    {...props}
  />
);

const activeProps = {
  position: 'absolute',
  left: 0,
  right: 0,
  bottom: 0,
  marginLeft: 'auto',
  marginRight: 'auto',
  width: '50%',
  height: '2px',
  borderRadius: '0.75rem',
  content: '""',
  transition: 'width: 0.5s, opacity: 0.5s, transform 0.5s',
  transform: 'translateY(-10px)',
};

const NavLink = ({ isActive, ...props }) => {
  const { colorMode } = useColorMode();
  return (
    <Link
      p={4}
      mr={8}
      pos="relative"
      color={text[colorMode]}
      fontWeight="medium"
      transition="all 0.2s"
      _hover={{
        color: textHover[colorMode],
        textDecoration: 'none',
        transform: `translateY(-2px)`,
      }}
      _after={isActive ? { backgroundColor: accent[colorMode], ...activeProps } : null}
      {...props}
    />
  );
};

const Items = ({ side }) => {
  const { pathname } = useRouter();
  const navItems = [];
  const { nav } = useConfig();
  nav[side].map(item => {
    navItems.push(
      <NavLink key={item.link} href={item.link} isActive={pathname === item.link}>
        {item.title}
      </NavLink>,
    );
  });
  return navItems;
};

export const NavbarDesktop = props => {
  const { colorMode } = useColorMode();
  const { colors } = useTheme();
  const logo = { dark: 'white', light: colors.original.primary };
  return (
    <Header {...props}>
      <Navbar>
        <Link href="/">
          <Logo.Typographic color={logo[colorMode]} width={160} />
        </Link>
        <ItemGroup>
          <Items side="left" />
          <Items side="right" />
          <NavLink href="https://docs.stellar.tech">Docs</NavLink>
          <Button href="/contact" variant="outline" variantColor={variant[colorMode]}>
            Talk to Us
          </Button>
        </ItemGroup>
      </Navbar>
    </Header>
  );
};
