import React, { useEffect, useState } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import { FiChevronDown } from "react-icons/fi";
import { AngleSection } from "components/styled/sections";
import { useWindowScroll } from "react-use";
import site from "config";
import { LogoFull } from "components/Logo";
import styled, { keyframes } from "styled-components";
import { pulse } from "react-animations";
import theme from "styles/exports.module.scss";

const easing = t => 1 + --t * t * t * t * t;

const LogoBlock = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  pointer-events: auto;
  width: 100%;
  height: 86px;
  margin-top: 5vh;
  background-color: transparent !important;
  z-index: 1001;
`;

const HeroCol = styled(Col)`
  display: flex;
  justify-content: center;
`;
const pulseAnimation = keyframes`${pulse}`;

const ScrollIndicator = styled.div`
  display: flex;
  margin-left: auto;
  margin-right: auto;
  margin-top: auto;
  justify-content: center;
  align-content: flex-end;
`;

const ScrollButton = styled(Button)`
  display: inline-block;
  vertical-align: middle;
  text-align: center;
  user-select: none;
  background-color: transparent;
  border: none;
  padding: 0.375rem 0.75rem;
  font-size: 1rem;
  line-height: 1.5;

  &:hover {
    background-color: transparent;
    border-color: none;
  }
`;

const BouncingArrow = styled(FiChevronDown)`
  animation: 1s ${pulseAnimation} infinite;
`;

const HomeHeroSection = styled(AngleSection)`
  display: flex;
  padding-top: 3rem;
  margin-top: 3rem;
  margin-bottom: 3rem;
  flex-direction: column;
  min-height: 30vh;
  position: relative;
  -webkit-backface-visibility: hidden;

  & .row {
    justify-content: center;
  }

  & .hero-text-container {
    margin-top: 4vh;
  }

  & .hero-text {
    text-align: center;
    line-height: 1.2;
    font-size: ${theme.subDisplay3Size};
    font-weight: ${theme.fontWeightLight};
    color: white;
    text-shadow: 4px 2px 15px ${theme.stDark};
    letter-spacing: 0.05rem;
  }

  & .scroll-indicator {
    position: absolute;
    left: 50%;
    right: 50%;
    bottom: 0;
    z-index: 100;
  }
`;

function Hero(props) {
  const logoBreak = site.global.logoTransitionScroll;
  // State
  const [readyToScroll, setReadyToScroll] = useState(false);
  const { y } = useWindowScroll();

  // Hooks
  useEffect(() => {
    y >= logoBreak && setReadyToScroll(true);
    y > 0 && y < logoBreak && setReadyToScroll(false);
  }, [logoBreak, y]);
  return (
    <>
      <HomeHeroSection
        height="90vh"
        backgroundColor={theme.stPrimary}
        directionTop="flat"
        marginTop="0"
        style={{
          backgroundImage: theme.stGradientImage,
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundSize: "cover",
          WebkitBackfaceVisibility: "hidden"
        }}
      >
        <Container>
          <Row>
            <HeroCol sm={12}>
              <LogoBlock
                // className={readyToScroll ? "logo hidden" : "logo"}
                style={{
                  opacity: `${Math.max(easing(1 - y / logoBreak), 0)}`,
                  transform: `scale(${Math.max(easing(1 - y / logoBreak), 0) *
                    0.325 +
                    0.625}) translate3d(0, 0, 0)`,
                  transformOrigin: "top",
                  top: Math.max(logoBreak - y, 2)
                }}
              >
                <LogoFull color="white" width={400} />
              </LogoBlock>
            </HeroCol>
            <HeroCol className={"hero-text-container"} sm={12}>
              <p className={"hero-text"}>{site.pages.home.headings.text}</p>
            </HeroCol>
          </Row>
        </Container>
        <ScrollIndicator className={"scroll-indicator"}>
          <ScrollButton
            variant="link"
            title="Scroll to Info"
            onClick={props.scrollToSections}
          >
            <BouncingArrow size="3rem" color={theme.navLinkColor} />
          </ScrollButton>
        </ScrollIndicator>
      </HomeHeroSection>
    </>
  );
}

export default Hero;
