const functionalTheme = {
  primary: '#2b3c8f',
  secondary: '#2a174a',
  tertiary: '#b3e3f5',
  dark: '#161318',
  light: '#f0f0f0',
};
export const theme = {
  colors: {
    ...functionalTheme,
    gray: '#70707f',
    white: '#ffffff',
    black: '#000',
    green: '#48a9a6',
    red: '#ca2e55',
    yellow: '#E3B505',
    orange: '#F5AB00',
    pink: '#f08cae',
    blue: functionalTheme.primary,
    purple: functionalTheme.secondary,
    teal: functionalTheme.tertiary,
  },
  fonts: { body: 'Open Sans', mono: 'Fira Code' },
  fontWeights: {
    hairline: 300,
    thin: 300,
    light: 300,
    normal: 400,
    medium: 600,
    semibold: 600,
    bold: 800,
    extrabold: 800,
    black: 800,
  },
  radii: {
    none: '0',
    sm: '0.125rem',
    md: '0.25rem',
    lg: '0.5rem',
    xl: '0.75rem',
    '2xl': '1rem',
    '3xl': '1.25rem',
    '4xl': '1.5rem',
    '5xl': '1.75rem',
    '6xl': '2rem',
  },
};

export const nav = {
  left: [
    {
      title: 'Cloud',
      link: '/cloud',
    },
    {
      title: 'Services',
      link: '/services',
    },
    {
      title: 'Consulting',
      link: '/consulting',
    },
  ],
  right: [{ title: 'About', link: '/about' }],
};

export default {
  siteSlogan: 'Fueling your digital velocity',
  theme: theme,
  nav: nav,
};
