import * as React from 'react';
import { Box, Button, Flex, Stack } from '@chakra-ui/core';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import { WiDaySunny, WiStars } from 'react-icons/wi';
import { BsMoon, BsChevronUp } from 'react-icons/bs';
import { useColorMode } from '../context';

const bg = { dark: 'original.tertiary', light: 'original.secondary' };
const color = { dark: 'original.dark', light: 'original.light' };

const iconColor = { dark: 'yellow', light: 'red' };
const icon = { dark: WiDaySunny, light: BsMoon };

const scrollToTop = () => {
  if (typeof window === 'undefined') {
    return;
  }
  window.scrollTo({ top: 0, behavior: 'smooth' });
  window.document.activeElement.blur();
};

export const Controls = props => {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <Box
      pos="fixed"
      bottom="25px"
      right="25px"
      w="2rem"
      borderRadius="xl"
      h="6rem"
      py={4}
      bg={bg[colorMode]}
      color={color[colorMode]}
      zIndex={1}
      {...props}>
      <Stack pos="relative" flexDir="column" alignItems="center">
        <Button onClick={toggleColorMode} variant="unstyled" minW="unset" h="unset">
          <Box as={icon[colorMode]} color={iconColor[colorMode]} />
        </Button>
        <Button onClick={scrollToTop} variant="unstyled" minW="unset" h="unset">
          <Box as={BsChevronUp} color={iconColor[colorMode]} />
        </Button>
      </Stack>
    </Box>
  );
};
