import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import { isEqual } from 'lodash';

export const useActiveSection = (
  currentState,
  callback,
  defaultState,
  [...deps],
  [...sections],
) => {
  const last = sections.length - 1;
  const effect = ({ currPos }) => {
    const { y } = currPos;
    let toSet = defaultState;
    for (const [i, section] of sections.entries()) {
      const [ref, stateObj] = section;
      if (i === last) {
        if (y < -ref.current.offsetTop) {
          const sameAsCurrent = isEqual(currentState, stateObj);
          if (!sameAsCurrent) {
            toSet = stateObj;
            break;
            // callback(stateObj);
            // break;
          }
        }
      } else {
        const [nextRef] = sections[i + 1];
        console.log(y, -nextRef.current.offsetTop, -ref.current.offsetTop);
        if (y >= -nextRef.current.offsetTop && y <= -ref.current.offsetTop) {
          const sameAsCurrent = isEqual(currentState, stateObj);
          if (!sameAsCurrent) {
            toSet = stateObj;
            break;
            // callback(stateObj);
            // break;
          }
        } else {
          const sameAsCurrent = isEqual(currentState, defaultState);
          //   if (!sameAsCurrent) {
          //     callback(defaultState);
          //     // break;
          //   }
        }
      }
    }
    callback(toSet);
  };

  return useScrollPosition(effect, deps);
};
