import * as React from "react";
import { Box } from "@chakra-ui/core";

export const AngleSection = ({ color, children, ...props }) => {
  const subsections = React.Children.toArray(children);
  const Child = React.cloneElement(
    subsections[0],
    { position: "relative" },
    ...subsections.slice(1)
  );
  return (
    <Box overflow="hidden" position="relative" {...props}>
      <Box
        position="absolute"
        top={0}
        bottom={0}
        right={0}
        left={0}
        w="100%"
        h="100%"
        bg={color}
        transform="skewY(-6deg)"
        transformOrigin="top left"
      ></Box>
      <Child />
    </Box>
  );
};
