import * as React from "react";
import NextLink from "next/link";
import { Button as ChakraButton } from "@chakra-ui/core";
import { useLinkType } from "../hooks";

export const Button = ({ href, ...props }) => {
  return (
    <ChakraButton
      borderRadius="lg"
      px={3}
      py={1}
      lineHeight={1.5}
      aria-label={props.children}
      {...props}
    />
  );
};
