import * as React from 'react';
import { Box, Flex, Heading, Text } from '@chakra-ui/core';
import { useConfig, useColorMode, useTheme } from '../context';
import { VMware } from '../components/Icons';
import { Button } from '../components/Button';
import { Logo } from '../components/Logo';

const heroBg = { dark: 'original.dark', light: 'original.primary' };
const heroText = { dark: 'white', light: 'original.light' };
const cardBg = { dark: 'dark.800', light: 'white' };
const cardColor = { dark: 'white', light: 'black' };

const Card = ({ title = 'Title', content = 'Content', icon, ...props }) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      p={8}
      w="sm"
      h="sm"
      bg={cardBg[colorMode]}
      color={cardColor[colorMode]}
      borderRadius="md"
      boxShadow="2xl"
      {...props}>
      <Flex>
        <Heading as="h2" fontSize="xl">
          {title}
        </Heading>
      </Flex>
      <Flex>
        <Text ml={2}>{content}</Text>
      </Flex>
    </Box>
  );
};

const sect1Bg = { dark: 'whiteAlpha.50', light: 'gray.100' };
const sect2Bg = { dark: 'whiteAlpha.100', light: 'original.dark' };
const sect2Text = { dark: 'original.light', light: 'original.light' };

const Home = () => {
  const { colorMode } = useColorMode();
  const { siteSlogan } = useConfig();
  const { colors } = useTheme();
  const logo = { dark: 'white', light: 'white' };
  return (
    <>
      <Box w="100%" minH="80vh" bg={heroBg[colorMode]} color={heroText[colorMode]} px={24} pt={32}>
        <Flex flexDir="column" alignItems="center">
          <Logo.Typographic color={logo[colorMode]} width={512} />
          <Flex textAlign="center">
            <Heading as="h1" fontSize="2xl" fontWeight="light" mb={32}>
              {siteSlogan}
            </Heading>
          </Flex>
        </Flex>
        <Flex pos="relative" mt={32} h="160px">
          <Flex justifyContent="space-between" pos="absolute" w="100%" h="sm">
            <Card />
            <Card />
            <Card />
          </Flex>
        </Flex>
      </Box>
      <Box as="section" pt="320px" overflow="hidden" bg={sect1Bg[colorMode]}>
        <Flex h="100%" overflow="hidden" px={24} py={16} alignItems="center" flexDir="column">
          <Heading as="h3" fontSize="4xl">
            Orion
          </Heading>
          <Text
            my={16}
            maxW={[null, null, '60%']}
            whiteSpace="pre-line"
            fontSize="lg"
            textAlign="justify">
            We're not just knowledgeable about cloud technologies, we've actually built our own
            cloud. Enterprises need tight control over their technology, so we decided to built the
            most customizable cloud platform on the planet. Powered by{' '}
            <VMware height={14} mx={1} display="inline" />, the Orion platform fits right in the
            enterprise ecosystem.
          </Text>
          <Button href="/cloud" leftIcon="chevron-right" variantColor="primary">
            Gravitate to Orion
          </Button>
        </Flex>
      </Box>
      <Box as="section" pt={24} overflow="hidden" bg={sect2Bg[colorMode]}>
        <Flex
          h="100%"
          overflow="hidden"
          px={24}
          py={16}
          alignItems="center"
          flexDir="column"
          color={sect2Text[colorMode]}>
          <Heading as="h3" fontSize="4xl">
            Dedicated Services
          </Heading>
          <Text
            my={16}
            maxW={[null, null, '60%']}
            whiteSpace="pre-line"
            fontSize="lg"
            textAlign="justify">
            As a skilled team of engineers who have supported businesses and technologies of all
            types for decades at a global scale, we're experts at meeting the demands of operating
            business technology. With our bespoke, dedicated IT services, your business is able to
            offload IT burdens based on its specific needs — be it end user IT support,
            infrastructure management, system monitoring, or the entire IT environment.
          </Text>
          <Button href="/services" leftIcon="chevron-right" variantColor="primary">
            Upgrade your IT Team
          </Button>
        </Flex>
      </Box>
    </>
  );
};

export default Home;
