import * as React from "react";
import { Box, Flex } from "@chakra-ui/core";
import { AngleSection } from "../AngleSection";

export const Section = ({
  icon,
  right = false,
  left = true,
  children,
  ...props
}) => {
  const Icon = props => <Box as={icon} size="12rem" color="white" {...props} />;
  return (
    <AngleSection color="original.dark" {...props}>
      <Flex
        h="60vh"
        pos="relative"
        flexDir="row"
        alignItems="center"
        justifyContent="center"
      >
        {left && !right && <Icon />}
        <Flex flex="1 0 auto" flexDir="column">
          {children}
        </Flex>
        {!left && right && <Icon />}
      </Flex>
    </AngleSection>
  );
};
