export const Stars = props => {
  const { stars } = useConfig();
  const Base = props => <Particles params={stars} {...props} />;
  return (
    <Box
      pos="absolute"
      bottom={0}
      top={0}
      right={0}
      left={0}
      className="__stars-container"
      {...props}>
      <Box as={Base}></Box>
    </Box>
  );
  //   return <Box as={Base} pos="fixed" top={0} bottom={0} right={0} left={0} {...props} />;
};
