import * as React from "react";
import { Box } from "@chakra-ui/core";

export const AngleSection = ({ color, children, ...props }) => {
  return (
    <Box overflow="hidden" position="relative" {...props}>
      <Box
        // position="absolute"
        // top={0}
        // bottom={0}
        // right={0}
        // left={0}
        w="100%"
        h="100%"
        bg={color}
        transform="skewY(-6deg)"
        transformOrigin="top left"
      >
        <Box pos="relative" transform="skewY(6deg)" zIndex={400}>
          {children}
        </Box>
      </Box>
    </Box>
  );
};
