import * as React from "react";
import NextLink from "next/link";
import { Box, Flex, Link } from "@chakra-ui/core";
import { Logo } from "../Logo";
import { Button } from "../Button";
import { useConfig } from "../../context";

const Header = props => {
  return (
    <Box
      as="header"
      pos="fixed"
      height={20}
      top={0}
      left={0}
      right={0}
      w="100%"
      zIndex={1000}
      bg="original.primary"
      {...props}
    />
  );
};

const Navbar = props => {
  return (
    <Flex
      as="nav"
      pos="relative"
      px={4}
      py={2}
      alignItems="center"
      flexDir="row"
      flexWrap="nowrap"
      justifyContent="flex-start"
      {...props}
    />
  );
};

const ItemGroup = props => (
  <Flex
    flex="1 0 0"
    alignItems="center"
    pointerEvents="auto"
    justifyContent="center"
    {...props}
  />
);

const NavLink = ({ isLocation, active, href, label, ...props }) => {
  return (
    <NextLink href={href} passHref>
      <Link pos="relative" d="inline-block" p={4} mr={8} {...props}>
        {label}
      </Link>
    </NextLink>
  );
};

const Items = ({ side, location, ...props }) => {
  const navItems = [];
  const { nav } = useConfig();
  nav[side].map(item => {
    navItems.push(
      <NavLink key={item.link} href={item.link} label={item.title} />
    );
  });
  return navItems;
};

export const NavbarDesktop = props => {
  return (
    <Header {...props}>
      <Navbar>
        <ItemGroup>
          <Items side="left" />
        </ItemGroup>
        <NextLink href="/">
          <Logo.Typographic color="white" width={160} />
        </NextLink>
        <ItemGroup>
          <Items side="right" />
          <Button
            href="/contact"
            variant="outline"
            variantColor="white"
            _hover={{
              color: "original.dark",
              borderColor: "white",
              backgroundColor: "whiteAlpha.500"
            }}
          >
            Talk to Us
          </Button>
        </ItemGroup>
      </Navbar>
    </Header>
  );
};
