import * as React from "react";
import { createRef } from "react";
import Hero from "../src/components/pages/home/Hero";
import { Sections } from "../src/components/pages/home/Sections";

const Home = props => {
  const sectionsRef = createRef();
  const scrollToSections = () => {
    sectionsRef.current.scrollIntoView({
      behavior: "smooth",
      block: "center",
      inline: "nearest"
    });
  };
  return (
    <>
      <Hero scrollToSections={scrollToSections} />
      <Sections styledRef={sectionsRef} />
    </>
  );
};

export default Home;
