import * as React from "react";
import NextLink from "next/link";
import { Box, Flex, Link } from "@chakra-ui/core";
import { Logo } from "../Logo";
import { useConfig } from "../../context";

const Header = props => (
  <Box
    as="header"
    pos="fixed"
    height={20}
    top={0}
    w="100%"
    zIndex={1000}
    bg="transparent"
    {...props}
  />
);

const Navbar = props => {
  return (
    <Flex
      as="nav"
      pos="relative"
      px={4}
      py={2}
      alignItems="center"
      flexDir="row"
      flexWrap="nowrap"
      justifyContent="flex-start"
      {...props}
    />
  );
};

const ItemGroup = props => (
  <Flex
    flex="1 0 0"
    alignItems="center"
    pointerEvents="auto"
    justifyContent="center"
    {...props}
  />
);

const NavLink = ({ isLocation, active, href, label, ...props }) => {
  return (
    <NextLink href={href} passHref>
      <Link {...props}>{label}</Link>
    </NextLink>
  );
};

const Items = ({ side, location, ...props }) => {
  const navItems = [];
  const { newNav } = useConfig();
  newNav[side].map(item => {
    navItems.push(
      <NavLink key={item.link} href={item.link} label={item.title} />
    );
  });
  return navItems;
};

export const NavbarDesktop = props => {
  return (
    <Header {...props}>
      <Navbar>
        <ItemGroup>
          <Items side="left" />
        </ItemGroup>
        <NextLink href="/">
          <Logo.Typographic color="white" width={160} height={160} />
        </NextLink>
      </Navbar>
    </Header>
  );
};
