import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import { isEqual } from 'lodash';

export const useActiveSection = (callback, [...deps], [...sections]) => {
  const last = sections.length - 1;
  const effect = ({ currPos }) => {
    const { y } = currPos;
    for (const [i, section] of sections.entries()) {
      const [ref, stateObj] = section;
      if (i === last) {
        if (y < -ref.current.offsetTop) {
          const sameAsCurrent = isEqual(currentState, stateObj);
          if (!sameAsCurrent) {
            callback(stateObj);
            break;
          }
        }
      } else {
        const [nextRef] = sections[i + 1];
        if (y >= nextRef.current.offsetTop && y <= -ref.current.offsetTop) {
          const sameAsCurrent = isEqual(currentState, stateObj);
          if (!sameAsCurrent) {
            callback(stateObj);
            break;
          }
        }
      }
    }
  };

  return useScrollPosition(effect, [...deps]);
};
