import * as React from 'react';
import { Box, Grid, Heading, Text } from '@chakra-ui/core';
import { BLOCKS, MARKS } from '@contentful/rich-text-types';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';

const Title = props => <Heading as="h3" fontSize="4xl" {...props} />;
const Subtitle = props => <Heading as="h4" fontSize="xl" fontWeight="light" {...props} />;
const Body = props => (
  <Text
    my={16}
    maxW={[null, null, '60%']}
    whiteSpace="pre-line"
    fontSize="lg"
    textAlign="justify"
    {...props}
  />
);

const Subsections = props => {
  return (
    <Grid templateColumns="repeat(2, 1fr)" gap={16} my={16} maxW={[null, null, '80%']}>
      <Box {...props} />
    </Grid>
  );
};

const Subsection = ({ title, body }) => {
  return (
    <Box>
      <Heading as="h4" fontSize="lg" mb={4}>
        {title}
      </Heading>
      <Text whiteSpace="pre-line" fontSize="lg" textAlign="justify">
        {documentToReactComponents(body)}
      </Text>
    </Box>
  );
};

export const usePageContent = (rawContent, referenceItems) => {
  let obj = {};
  let error;
  console.log(rawContent);
  try {
    const {
      title: rawTitle,
      subtitle: rawSubtitle,
      body: bodyObj,
      button: showButton,
      buttonLink: rawButtonLink,
      buttonText: rawButtonText,
      paragraphs,
    } = rawContent.fields;
    let body = null;
    let subsections = null;
    if (bodyObj) {
      body = <Body>{documentToReactComponents(bodyObj)}</Body>;
    }
    if (paragraphs.length !== 0) {
      subsections = (
        <Subsections>
          {paragraphs.map((p, i) => {
            const matching = referenceItems.Entry.map(t => t.sys.id === p.sys.id)[0];
            return <Subsection key={i} title={matching.fields.title} body={matching.fields.body} />;
          })}
        </Subsections>
      );
    }
    console.log(pageSectionParagraph);
    obj = {
      title: <Title>{rawTitle}</Title>,
      subtitle: <Subtitle>{rawSubtitle}</Subtitle>,
      buttonText: rawButtonText,
      buttonLink: rawButtonLink,
      showButton,
      body,
      subsections,
    };
  } catch (err) {
    error = err.message;
  }
  return { error, ...obj };
};
