import * as React from 'react';
import { forwardRef } from 'react';
import { Box, Tooltip } from '@chakra-ui/core';
import { MdMyLocation } from 'react-icons/md';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const MarkerIcon = props => <Box as={MdMyLocation} {...props} />;

export const USMap = forwardRef(({ geoData, locations }, ref) => {
  return (
    <Box ref={ref}>
      <ComposableMap projection="geoAlbersUsa">
        <Geographies geography={geoData}>
          {({ geographies }) => (
            <>
              {geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  // stroke="#FFF"
                  geography={geo}
                  fill="#DDD"
                />
              ))}
              {locations &&
                locations.map(loc => {
                  if (loc.active) {
                    return (
                      <Tooltip label={loc.displayName} key={loc.id}>
                        <g>
                          <Marker coordinates={[loc.coordinates.lon, loc.coordinates.lat]}>
                            <MarkerIcon />
                          </Marker>
                        </g>
                      </Tooltip>
                    );
                  }
                })}
            </>
          )}
        </Geographies>
      </ComposableMap>
    </Box>
  );
});
