const functionalTheme = {
  primary: "#2b3c8f",
  secondary: "#2a174a",
  tertiary: "#b3e3f5",
  dark: "#161318",
  light: "#f0f0f0"
};
export const theme = {
  colors: {
    ...functionalTheme,
    gray: "#70707f",
    white: "#ffffff",
    black: "#000",
    green: "#48a9a6",
    red: "#ca2e55",
    yellow: "#E3B505",
    orange: "#F5AB00",
    pink: "#f08cae",
    blue: functionalTheme.primary,
    purple: functionalTheme.secondary,
    teal: functionalTheme.tertiary
  },
  fonts: { body: "Open Sans", mono: "Fira Code" }
};

export const nav = {
  left: [
    {
      title: "Cloud",
      link: "/cloud"
    },
    {
      title: "Services",
      link: "/services"
    },
    {
      title: "Consulting",
      link: "/consulting"
    }
  ],
  right: [{ title: "About", link: "/about" }]
};

export default {
  theme: theme,
  nav: nav
};
