import { createClient, EntryCollection, ContentTypeLink } from 'contentful';

interface PageAttrs {
  id: string;
  slug: string;
  title: string;
  subtitle?: string;
}

interface ContentRef {
  sys: ContentTypeLink;
}

interface PageContent {
  page: ContentRef;
  sortWeight: number;
  title: string;
  subtitle?: string;
  paragraphs: ContentRef[];
  button: boolean;
  buttonText?: string;
  buttonLink?: string;
}

const client = createClient({
  space: process.env.NEXT_PUBLIC_CONTENTFUL_SPACE,
  accessToken: process.env.NEXT_PUBLIC_CONTENTFUL_ACCESS_TOKEN,
});

/**
 * Query Contentful for a specific content_type
 */
export const contentQuery = async (
  contentType: string,
  query?: object,
): Promise<EntryCollection<any>> => {
  let queryParams = { content_type: contentType };
  if (query) {
    queryParams = { ...queryParams, ...query };
  }
  try {
    const entries = await client.getEntries(queryParams);
    return entries;
  } catch (err) {
    console.error(err);
    throw err;
  }
};

/**
 * Get all pages
 */
export const getPages = async (): Promise<PageAttrs[]> => {
  let pages = [];
  const data = await contentQuery('page');
  if (data.total !== 0) {
    pages = data.items.map(e => ({ id: e.sys.id, ...Object(e.fields) }));
  }
  return pages;
};

/**
 * Get Page by slug
 */
export const getPage = async (pageSlug: string): Promise<PageAttrs> => {
  let page = { id: '', title: '', slug: '' };
  const data = await contentQuery('page', { 'fields.slug': pageSlug });
  if (data.total !== 0) {
    page = { id: data.items[0].sys.id, ...Object(data.items[0].fields) };
  }
  console.dir(page);
  return page;
};

/**
 * Get content for a specific page by its sys.id
 */
export const getPageContent = async (pageId: string): Promise<PageContent[]> => {
  let pageContent = [];
  const data = await contentQuery('pageContent', { 'fields.page.sys.id': pageId });

  if (data.total !== 0) {
    const items = data.items;
    const getRefValue = (obj: any): any => {
      for (let k of Object.keys(obj)) {
        const item = obj[k];
        if (item.constructor.name === 'Object' && 'sys' in Object(item)) {
          for (let ref of data.includes) {
            if (ref?.sys?.id === item.sys.id) {
              return { id: ref.sys.id, ...item.fields };
            }
          }
        } else if (item.constructor.name === 'Array') {
          return item.map(i => getRefValue(i));
        } else {
          return obj;
        }
      }
    };

    for (let i of items) {
      for (let [k, v] of Object.entries(i.fields)) {
        pageContent[k] = getRefValue(v);
      }
    }

    // pageContent = data.items.map(i => i.fields);
  }
  console.log(pageContent);
  return pageContent;
};
