import * as React from 'react';
import { Box, Flex, Heading, Text } from '@chakra-ui/core';
import { useConfig, useColorMode, useTheme } from '../context';
import { Logo } from '../components/Logo';

const heroBg = { dark: 'original.dark', light: 'original.primary' };
const heroText = { dark: 'white', light: 'original.light' };
const cardBg = { dark: 'dark.800', light: 'white' };
const cardColor = { dark: 'white', light: 'black' };

const Card = ({ title = 'Title', content = 'Content', icon, ...props }) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      p={8}
      w="sm"
      h="sm"
      bg={cardBg[colorMode]}
      color={cardColor[colorMode]}
      borderRadius="md"
      boxShadow="2xl"
      {...props}>
      <Flex>
        <Heading as="h2" fontSize="xl">
          {title}
        </Heading>
      </Flex>
      <Flex>
        <Text ml={2}>{content}</Text>
      </Flex>
    </Box>
  );
};

const sectBg1 = { dark: 'whiteAlpha.50', light: 'gray.100' };

const Home = () => {
  const { colorMode } = useColorMode();
  const { siteSlogan, sections } = useConfig();
  const { colors } = useTheme();
  const logo = { dark: 'white', light: 'white' };
  return (
    <>
      <Box w="100%" minH="80vh" bg={heroBg[colorMode]} color={heroText[colorMode]} px={24} pt={32}>
        <Flex flexDir="column" alignItems="center">
          <Logo.Typographic color={logo[colorMode]} width={512} />
          <Flex textAlign="center">
            <Heading as="h1" fontSize="2xl" fontWeight="light" mb={32}>
              {siteSlogan}
            </Heading>
          </Flex>
        </Flex>
        <Flex pos="relative" mt={32} h="160px">
          <Flex justifyContent="space-between" pos="absolute" w="100%" h="sm">
            <Card />
            <Card />
            <Card />
          </Flex>
        </Flex>
      </Box>
      <Box pt="320px" overflow="hidden" bg={sectBg1[colorMode]}>
        <Flex
          as="section"
          h="100%"
          overflow="hidden"
          px={24}
          py={16}
          alignItems="center"
          flexDir="column">
          <Heading as="h3" fontSize="4xl">
            Orion
          </Heading>
          <Text my={16}>Stuff about Orion</Text>
        </Flex>
      </Box>
    </>
  );
};

export default Home;
