import * as React from 'react';
import { Box } from '@chakra-ui/core';
import { useConfig, useColorMode } from '../context';
import { Logo } from '../components/Logo';

const heroBg = { dark: 'whiteAlpha.300', light: 'original.tertiary' };
const heroText = { dark: 'white', light: 'original.dark' };

const Home = () => {
  const { colorMode } = useColorMode();
  const { siteSlogan } = useConfig();
  return (
    <>
      <Box
        w="100%"
        h="50vh"
        bg={heroBg[colorMode]}
        mt={4}
        color={heroText[colorMode]}
        p={24}
        borderRadius="5xl">
        Content
      </Box>
    </>
  );
};

export default Home;
