import * as React from 'react';
import { Box, Flex, Stack } from '@chakra-ui/core';
import { WiDaySunny, WiStars } from 'react-icons/wi';
import { BsMoon } from 'react-icons/bs';
import { useColorMode } from '../context';

const bg = { dark: 'original.tertiary', light: 'original.secondary' };
const color = { dark: 'original.dark', light: 'original.light' };

const iconColor = { dark: 'yellow', light: 'red' };
const icon = { dark: WiDaySunny, light: BsMoon };

export const Controls = props => {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <Box
      pos="fixed"
      bottom="25px"
      right="25px"
      w="2rem"
      borderRadius="xl"
      h="6rem"
      py={4}
      bg={bg[colorMode]}
      color={color[colorMode]}>
      <Stack pos="relative" flexDir="column" alignItems="center">
        <Flex>
          <Box onClick={toggleColorMode} as={icon[colorMode]} color={iconColor[colorMode]} />
        </Flex>
      </Stack>
    </Box>
  );
};
