import * as React from 'react';
import { forwardRef } from 'react';
import {
  Box,
  Link,
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverArrow,
} from '@chakra-ui/core';
import { Pin } from './Icons';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const Circle = props => <Box as="circle" {...props} />;

const MapMarker = forwardRef(({ color = 'currentColor', ...props }, ref) => {
  return (
    <Marker {...props}>
      {/* <Pin ref={ref} color={color} /> */}
      <Circle r={4} fill={color} stroke="white" boxShadow="md" strokeWidth={2} />
    </Marker>
  );
});

export const USMap = forwardRef(({ geoData, locations, mapColor, markerColor, ...props }, ref) => {
  return (
    <Box ref={ref} mx="auto" {...props}>
      <ComposableMap projection="geoAlbersUsa" style={{ zIndex: 1, position: 'relative' }}>
        <Geographies geography={geoData}>
          {({ geographies }) => (
            <>
              {geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill={mapColor}
                  style={{
                    hover: { outline: 'none' },
                    pressed: { outline: 'none' },
                    default: { outline: 'none' },
                  }}
                />
              ))}
              {locations &&
                locations.map(loc => {
                  if (loc.active) {
                    return (
                      <Popover key={loc.id} trigger="hover" placement="top" usePortal>
                        <PopoverTrigger>
                          <Link href="#" transform="translateY(-1.5em) translateX(-0.9em)">
                            <MapMarker
                              coordinates={[loc.coordinates.lon, loc.coordinates.lat]}
                              color={markerColor}
                            />
                          </Link>
                        </PopoverTrigger>
                        <PopoverContent zIndex={4} border={0}>
                          <PopoverHeader pt={4} fontWeight="bold" border={0}>
                            {loc.displayName}
                          </PopoverHeader>
                          <PopoverBody>{loc.description}</PopoverBody>
                          <PopoverArrow />
                        </PopoverContent>
                      </Popover>
                    );
                  }
                })}
            </>
          )}
        </Geographies>
      </ComposableMap>
    </Box>
  );
});
