import * as React from 'react';
import { Box, IconButton, Stack } from '@chakra-ui/core';
import { WiDaySunny, WiStars } from 'react-icons/wi';
import { useColorMode } from '../context';

const bg = { dark: 'original.tertiary', light: 'original.secondary' };
const color = { dark: 'original.dark', light: 'original.light' };

const iconColor = { dark: 'yellow', light: 'red' };
const icon = { dark: WiDaySunny, light: WiStars };

export const Controls = props => {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <Box
      pos="fixed"
      bottom="25px"
      right="25px"
      w="2rem"
      borderRadius="xl"
      h="6rem"
      bg={bg[colorMode]}
      color={color[colorMode]}>
      <Stack pos="relative">
        <IconButton variant="ghost" variantColor={iconColor[colorMode]} icon={icon[colorMode]} />
      </Stack>
    </Box>
  );
};
