import * as React from 'react';
import { forwardRef } from 'react';
import { Box, Tooltip } from '@chakra-ui/core';
import { MdMyLocation } from 'react-icons/md';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

// const MarkerIcon = props => <Box as={MdMyLocation} {...props} />;

const MarkerIcon = forwardRef(({ size = 19, color = 'currentColor', ...props }, ref) => {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 19 19"
      height={size}
      width={size}
      ref={ref}
      {...props}>
      <rect fill="none" x="0" y="0" width="19" height={size} />
      <path
        fill={color}
        transform="translate(2 2)"
        d="M7.5,14.941l-.4-.495c-.973-1.189-4.9-6.556-4.9-9.16A5.066,5.066,0,0,1,7.036,0q.222-.01.445,0a5.066,5.066,0,0,1,5.286,4.836q.01.225,0,.45c0,2.213-2.669,6.111-4.678,8.851ZM7.481.986a4.077,4.077,0,0,0-4.3,4.3c0,1.832,2.759,6.038,4.286,8.034,1.25-1.71,4.315-5.989,4.315-8.034a4.077,4.077,0,0,0-4.3-4.3Z"
      />
    </svg>
  );
});

export const USMap = forwardRef(({ geoData, locations, mapColor }, ref) => {
  return (
    <Box ref={ref}>
      <ComposableMap projection="geoAlbersUsa">
        <Geographies geography={geoData}>
          {({ geographies }) => (
            <>
              {geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  // stroke="#FFF"
                  geography={geo}
                  fill={mapColor}
                />
              ))}
              {locations &&
                locations.map(loc => {
                  if (loc.active) {
                    return (
                      <Tooltip label={loc.displayName} key={loc.id}>
                        <Marker coordinates={[loc.coordinates.lon, loc.coordinates.lat]}>
                          <MarkerIcon />
                        </Marker>
                      </Tooltip>
                    );
                  }
                })}
            </>
          )}
        </Geographies>
      </ComposableMap>
    </Box>
  );
});
