import * as React from "react";
import NextLink from "next/link";
import { Box, Flex } from "@chakra-ui/core";
import { Logo } from "../Logo";
import { Button } from "../Button";
import { Link } from "../Link";
import { useConfig } from "../../context";

const navLinkStyles = {
  p: 4,
  mr: 8,
  color: "whiteAlpha.800",
  fontWeight: "medium",
  _hover: { textDecoration: "none", color: "white" }
};

const Header = props => {
  return (
    <Box
      as="header"
      pos="fixed"
      height={20}
      top={0}
      left={0}
      right={0}
      w="100%"
      zIndex={1000}
      bg="original.primary"
      {...props}
    />
  );
};

const Navbar = props => {
  return (
    <Flex
      as="nav"
      pos="relative"
      px={4}
      py={2}
      alignItems="center"
      flexDir="row"
      flexWrap="nowrap"
      justifyContent="flex-start"
      {...props}
    />
  );
};

const ItemGroup = props => (
  <Flex
    flex="1 0 0"
    alignItems="center"
    pointerEvents="auto"
    justifyContent="center"
    {...props}
  />
);

const Items = ({ side, location }) => {
  const navItems = [];
  const { nav } = useConfig();
  nav[side].map(item => {
    navItems.push(
      <Link key={item.link} href={item.link} {...navLinkStyles}>
        {item.title}
      </Link>
    );
  });
  return navItems;
};

export const NavbarDesktop = props => {
  return (
    <Header {...props}>
      <Navbar>
        <ItemGroup>
          <Items side="left" />
        </ItemGroup>
        <NextLink href="/">
          <Logo.Typographic color="white" width={160} />
        </NextLink>
        <ItemGroup>
          <Items side="right" />
          <Link href="https://docs.stellar.tech" {...navLinkStyles}>
            Docs
          </Link>
          <Button
            href="/contact"
            variant="outline"
            _hover={{
              color: "original.dark",
              borderColor: "whiteAlpha.900",
              backgroundColor: "whiteAlpha.900"
            }}
          >
            Talk to Us
          </Button>
        </ItemGroup>
      </Navbar>
    </Header>
  );
};
