import * as React from 'react';
import { forwardRef } from 'react';
import {
  Box,
  Link,
  Popover,
  PopoverTrigger,
  PopoverContent,
  PopoverHeader,
  PopoverBody,
  PopoverArrow,
} from '@chakra-ui/core';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const MapMarker = forwardRef(({ color = 'currentColor', ...props }, ref) => {
  return (
    <Marker {...props}>
      <circle ref={ref} r={4} fill={color} strokeWidth={2} />
    </Marker>
  );
});

export const USMap = forwardRef(({ geoData, locations, mapColor, markerColor }, ref) => {
  return (
    <Box ref={ref}>
      <ComposableMap projection="geoAlbersUsa" style={{ zIndex: 1, position: 'relative' }}>
        <Geographies geography={geoData}>
          {({ geographies }) => (
            <>
              {geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  geography={geo}
                  fill={mapColor}
                  style={{
                    hover: { outline: 'none' },
                    pressed: { outline: 'none' },
                    default: { outline: 'none' },
                  }}
                />
              ))}
              {locations &&
                locations.map(loc => {
                  if (loc.active) {
                    return (
                      <Popover trigger="hover" placement="top" usePortal key={loc.id}>
                        <PopoverTrigger>
                          <Link href="#">
                            <MapMarker
                              coordinates={[loc.coordinates.lon, loc.coordinates.lat]}
                              color={markerColor}
                            />
                          </Link>
                        </PopoverTrigger>
                        <PopoverContent zIndex={4} border={0}>
                          <PopoverHeader pt={4} fontWeight="bold" border={0}>
                            {loc.displayName}
                          </PopoverHeader>
                          <PopoverBody>{loc.description}</PopoverBody>
                          <PopoverArrow />
                        </PopoverContent>
                      </Popover>
                    );
                  }
                })}
            </>
          )}
        </Geographies>
      </ComposableMap>
    </Box>
  );
});
