import * as React from "react";
import { Box, CSSReset } from "@chakra-ui/core";
import { Global } from "@emotion/core";
import { NavbarDesktop } from "../components/Nav";
import { useTheme } from "../context";

import smoothscroll from "smoothscroll-polyfill";

// const Stars = dynamic(() => import("../src/components/stars/particles"));

if (typeof window !== "undefined") {
  smoothscroll.polyfill();
}

const SiteContainer = props => {
  return <Box className="site-container" minH="100vh" h="100%" {...props} />;
};

const Main = props => <Box as="main" mt={20} overflowX="hidden" {...props} />;

export const SiteLayout = ({ children }) => {
  const { colors, fontWeights } = useTheme();
  return (
    <>
      <CSSReset />
      <SiteContainer>
        <NavbarDesktop />
        <Main>
          <Box minH="70vh">{children}</Box>
        </Main>
      </SiteContainer>
      <Global
        styles={{
          body: {
            backgroundColor: colors.original.dark,
            color: colors.original.light
          },
          a: { fontWeight: fontWeights.normal }
        }}
      />
    </>
  );
};
