import * as React from 'react';
import { useEffect, useRef } from 'react';
import useSWR from 'swr';
import { Box, Flex, Heading, Text } from '@chakra-ui/core';
import { useScrollPosition } from '@n8tb1t/use-scroll-position';
import { useConfig, useColorMode, useTheme, useGlobalState } from '../context';
import { USMap } from '../components/USMap';
import { VMware } from '../components/Icons';
import { Logo } from '../components/Logo';
import { Br, Button } from '../components';

const headerBg = { dark: 'transparent', light: 'original.light' };
const heroText = { dark: 'white', light: 'original.primary' };
const cardBg = { dark: 'whiteAlpha.100', light: 'white' };
const cardColor = { dark: 'white', light: 'original.dark' };
const sect1Bg = { dark: 'original.dark', light: 'original.light' };
const sect1Text = { dark: 'original.light', light: 'original.dark' };
const sect1BtnBg = { dark: 'original.light', light: 'original.light' };
const sect1BtnText = { dark: 'original.light', light: 'original.dark' };
const sect2Bg = { dark: 'original.dark', light: 'original.tertiary' };
const sect2Text = { dark: 'original.light', light: 'original.dark' };
const sect3Bg = { dark: 'original.dark', light: 'original.primary' };
const sect3Text = { dark: 'original.light', light: 'original.light' };
const sectBorder = {
  dark: { borderBottomColor: 'original.tertiary', borderBottomWidth: '1px' },
  light: {},
};

const fetcher = async url => {
  try {
    const response = await fetch(url);
    return await response.json();
  } catch (err) {
    console.error(err);
  }
};

// const gradientLight = {
//   background: 'rgb(22, 19, 24)',
//   background:
//     'linear-gradient(180deg, rgba(22, 19, 24, 1) 0%, rgba(42, 23, 74, 1) 50%, rgba(43, 60, 143, 1) 100%)',
// };

// const gradientLight = {
//   background: 'rgb(240, 240, 240)',
//   background: 'linear-gradient(180deg, rgba(43, 60, 143, 1) 10%, rgba(240, 240, 240, 1) 100%)',
// };

// const gradientLight = {
//   background: 'rgb(240, 240, 240)',
//   background: 'linear-gradient(180deg, rgba(240, 240, 240, 1) 0%,  rgba(43, 60, 143, 1) 100%)',
// };
const gradientLight = { backgroundColor: 'original.light' };

const gradientDark = {
  background: 'rgb(22, 19, 24)',
  background:
    'linear-gradient(180deg, rgba(43, 60, 143, 1) 0%, rgba(42, 23, 74, 1) 50%, rgba(22, 19, 24, 1) 100%)',
};

const gradient = { dark: gradientDark, light: gradientLight };

const Card = ({ title = 'Title', content = 'Content', icon, ...props }) => {
  const { colorMode } = useColorMode();
  return (
    <Box
      p={8}
      w="6xl"
      h="sm"
      bg={cardBg[colorMode]}
      color={cardColor[colorMode]}
      borderRadius="md"
      boxShadow="2xl"
      {...props}>
      <Flex>
        <Heading as="h2" fontSize="xl">
          {title}
        </Heading>
      </Flex>
      <Flex>
        <Text ml={2}>{content}</Text>
      </Flex>
    </Box>
  );
};

const Cloud = ({ locationUrl, geoData }) => {
  const { data: locationsRaw, error } = useSWR(locationUrl, fetcher);
  const { colorMode } = useColorMode();
  const { siteSlogan } = useConfig();
  const { colors } = useTheme();
  const mapColor = { dark: colors.whiteAlpha[200], light: colors.blackAlpha[200] };
  const markerColor = { dark: colors.original.tertiary, light: colors.original.primary };
  const { headerStyle, setHeaderStyle, headerLogo, setHeaderLogo } = useGlobalState();
  // !headerLogo && setHeaderLogo(true);
  // const logoRef = useRef();
  const heroRef = useRef();
  const sect1Ref = useRef();
  const sect2Ref = useRef();
  const sect3Ref = useRef();
  error && console.error(error);
  const locations = locationsRaw?.items?.map(loc => loc.fields);
  useScrollPosition(
    ({ currPos }) => {
      const { y } = currPos;
      // const logoTop = y <= -logoRef.current.offsetTop;
      // !headerLogo && logoTop && setHeaderLogo(true);
      // headerLogo && !logoTop && setHeaderLogo(false);
      let inView = 0;
      if (y >= -sect2Ref.current.offsetTop && y <= -sect1Ref.current.offsetTop) {
        inView = 1;
      } else if (y >= -sect3Ref.current.offsetTop && y <= -sect2Ref.current.offsetTop) {
        inView = 2;
      } else if (y < -sect3Ref.current.offsetTop) {
        inView = 3;
      } else {
        inView = 0;
      }
      switch (inView) {
        case 0:
          setHeaderStyle({ bg: headerBg[colorMode], color: sect1BtnText[colorMode] });
          break;
        case 1:
          setHeaderStyle({ bg: sect1Bg[colorMode], color: sect1Text[colorMode] });
          break;
        case 2:
          setHeaderStyle({ bg: sect2Bg[colorMode], color: sect2Text[colorMode] });
          break;
        case 3:
          setHeaderStyle({ bg: sect3Bg[colorMode], color: sect3Text[colorMode] });
          break;
        default:
          setHeaderStyle({ bg: headerBg[colorMode], color: sect1BtnText[colorMode] });
      }
    },
    [headerStyle, colorMode],
  );
  useEffect(() => {
    setHeaderStyle({ bg: headerBg[colorMode], color: sect1BtnText[colorMode] });
  }, [colorMode]);
  useEffect(() => {
    if (!headerLogo) {
      setHeaderLogo(true);
    }
  });
  return (
    <>
      <Box
        ref={heroRef}
        w="100%"
        minH="80vh"
        background={gradient[colorMode]}
        color={heroText[colorMode]}
        px={24}
        pt={32}>
        <Flex flexDir="column" alignItems="center" mt={[4, 4, 32]}>
          <Flex textAlign="center" flexDir="column">
            <Heading as="h1" fontSize="6xl" fontWeight="light">
              Orion
            </Heading>
            <Heading as="h2" fontSize="3xl" fontWeight="light">
              The Enterprise Native Cloud
            </Heading>
          </Flex>
        </Flex>
        <USMap
          geoData={geoData}
          locations={locations}
          mapColor={mapColor[colorMode]}
          markerColor={markerColor[colorMode]}
        />
      </Box>
      <Box
        ref={sect1Ref}
        as="section"
        pt="320px"
        pb={24}
        overflow="hidden"
        bg={sect1Bg[colorMode]}
        color={sect1Text[colorMode]}
        {...sectBorder[colorMode]}>
        <Flex h="100%" overflow="hidden" px={24} py={16} alignItems="center" flexDir="column">
          <Heading as="h3" fontSize="4xl">
            Orion
          </Heading>
          <Heading as="h4" fontSize="xl" fontWeight="light">
            The Enterprise Native Cloud Platform
          </Heading>
          <Text
            my={16}
            maxW={[null, null, '60%']}
            whiteSpace="pre-line"
            fontSize="lg"
            textAlign="justify">
            We're not just knowledgeable about cloud technologies, we've actually built our own
            cloud. Enterprises need tight control over their technology, so we decided to built the
            most customizable cloud platform on the planet. Powered by{' '}
            <VMware height={14} mx={1} display="inline" />, the Orion platform fits right in the
            enterprise ecosystem.
          </Text>
          <Button
            href="/cloud"
            leftIcon="chevron-right"
            color={sect1BtnText[colorMode]}
            variant={colorMode === 'light' ? 'outline' : 'solid'}
            borderColor={colorMode === 'light' ? 'black' : undefined}
            _hover={{
              backgroundColor: colorMode === 'light' ? 'blackAlpha.50' : 'whiteAlpha.500',
            }}>
