import * as React from 'react';
import { Box, CSSReset } from '@chakra-ui/core';
import { Global } from '@emotion/core';
import { NavbarDesktop } from '../components/Nav';
import { Controls } from '../components/Controls';
import { useTheme, useColorMode } from '../context';

import smoothscroll from 'smoothscroll-polyfill';

if (typeof window !== 'undefined') {
  smoothscroll.polyfill();
}

const Wrapper = props => <Box id="__wrapper" minH="100vh" h="100%" {...props} />;
const Main = props => <Box as="main" mt={32} overflowX="hidden" {...props} />;
const Root = props => <Box id="__content" minH="50vh" {...props} />;

export const SiteLayout = ({ children }) => {
  const { colors } = useTheme();
  const { colorMode } = useColorMode();

  const bg = { dark: colors.original.dark, light: 'white' };
  return (
    <>
      <CSSReset />
      <Wrapper>
        <NavbarDesktop />
        <Main>
          <Root>{children}</Root>
        </Main>
        <Controls />
      </Wrapper>
      <Global
        styles={{
          body: {
            backgroundColor: bg[colorMode],
            color: bg[!colorMode],
          },
        }}
      />
    </>
  );
};
