import * as React from "react";
import { Box, Flex } from "@chakra-ui/core";
import { AngleSection } from "../AngleSection";

export const Section = ({
  icon,
  right = false,
  left = true,
  children,
  ...props
}) => {
  const spacingProps = left && !right ? { ml: 12 } : { mr: 12 };
  const Icon = props => (
    <Flex maxW="75%" {...spacingProps}>
      <Box as={icon} size="16rem" color="white" {...props} />
    </Flex>
  );
  return (
    <AngleSection color="original.dark" {...props}>
      <Flex
        h="60vh"
        maxW="80%"
        pos="relative"
        mx="auto"
        px={4}
        flexDir="row"
        alignItems="center"
        justifyContent="center"
      >
        {left && !right && <Icon />}
        <Flex flex="1 0 auto" flexDir="column" {...spacingProps}>
          {children}
        </Flex>
        {!left && right && <Icon />}
      </Flex>
    </AngleSection>
  );
};
