import * as React from 'react';
import { Heading, Text } from '@chakra-ui/core';
import { BLOCKS, MARKS } from '@contentful/rich-text-types';
import { documentToReactComponents } from '@contentful/rich-text-react-renderer';

const Title = props => <Heading as="h3" fontSize="4xl" {...props} />;
const Subtitle = props => <Heading as="h4" fontSize="xl" fontWeight="light" {...props} />;
const Body = props => (
  <Text
    my={16}
    maxW={[null, null, '60%']}
    whiteSpace="pre-line"
    fontSize="lg"
    textAlign="justify"
    {...props}
  />
);

export const usePageContent = rawContent => {
  let obj = {};
  let error;
  try {
    const {
      title: rawTitle,
      subtitle: rawSubtitle,
      body: bodyObj,
      button: showButton,
      buttonLink: rawButtonLink,
      buttonText: rawButtonText,
    } = rawContent.fields;
    const body = documentToReactComponents(bodyObj);
    obj = {
      title: <Title>{rawTitle}</Title>,
      subtitle: <Subtitle>{rawSubtitle}</Subtitle>,
      body: <Body>{body}</Body>,
      showButton,
      buttonText: rawButtonText,
      buttonLink: rawButtonLink,
    };
  } catch (err) {
    error = err.message;
  }
  return { error, ...obj };
};
