import * as React from "react";
import { Box, Flex, Heading, Text } from "@chakra-ui/core";
import { Button } from "../Button";
import { AngleSection } from "../AngleSection";

export const Section = ({
  icon,
  right = false,
  left = true,
  title = "You forgot a title",
  subtitle = "You forgot a subtitle",
  button = "You forgot button text",
  content = "Placeholder content",
  ...props
}) => {
  const spacingProps = left && !right ? { ml: 32, px: 4 } : { mr: 32, px: 4 };
  const Icon = props => (
    <Flex maxW="75%" {...spacingProps}>
      <Box as={icon} size="16rem" color="white" {...props} />
    </Flex>
  );
  return (
    <AngleSection color="original.dark" {...props}>
      <Flex
        h="60vh"
        maxW="80%"
        pos="relative"
        mx="auto"
        px={4}
        flexDir="row"
        alignItems="center"
        justifyContent="center"
      >
        {left && !right && <Icon />}
        <Flex flex="1 0 auto" flexDir="column" {...spacingProps}>
          <Heading as="h3">{title}</Heading>
          <Heading as="h4">{subtitle}</Heading>
          <Text>{content}</Text>
          <Box>
            <Button mt={4} variant="outline">
              {button}
            </Button>
          </Box>
        </Flex>
        {!left && right && <Icon />}
      </Flex>
    </AngleSection>
  );
};
