import * as React from "react";
import { Box, CSSReset } from "@chakra-ui/core";
// import ScrollToTopOnMount from "../src/hooks/ScrollToTopOnMount";
// import NavBar from "../src/components/navbar";
// import Footer from "../src/components/footer";
// import ScrollToTop from "../src/components/ScrollToTop";

import smoothscroll from "smoothscroll-polyfill";

// const Stars = dynamic(() => import("../src/components/stars/particles"));

if (typeof window !== "undefined") {
  smoothscroll.polyfill();
}

const SiteContainer = props => {
  return <Box className="site-container" minH="100vh" h="100%" {...props} />;
};

const Main = props => (
  <Box as="main" pt={8} px={[2, 2, 5]} overflowX="hidden" {...props} />
);

export const SiteLayout = ({ children }) => {
  return (
    <>
      <CSSReset />
      <SiteContainer>
        <Main>
          <Box px={2} mt={16} minH="70vh">
            {children}
          </Box>
        </Main>
      </SiteContainer>
    </>
  );
};
