import * as React from 'react';
import { Box } from '@chakra-ui/core';
import Particles from 'react-particles-js';
import { useConfig } from '../context';

export const Stars = props => {
  const { stars } = useConfig();
  return (
    <Box
      as={Particles}
      pos="fixed"
      top={0}
      bottom={0}
      right={0}
      left={0}
      zIndex={-1}
      params={stars}
      {...props}
    />
  );
};
