import * as React from 'react';
import { forwardRef } from 'react';
import { Box, Tooltip } from '@chakra-ui/core';
import { MdMyLocation } from 'react-icons/md';
import { ComposableMap, Geographies, Geography, Marker } from 'react-simple-maps';

const MarkerIcon = forwardRef((props, ref) => {
  return (
    <Box ref={ref}>
      <Box as={MdMyLocation} {...props} />
    </Box>
  );
});

export const USMap = forwardRef(({ geoData, locations }, ref) => {
  return (
    <Box ref={ref}>
      <ComposableMap projection="geoAlbersUsa">
        <Geographies geography={geoData}>
          {({ geographies }) => (
            <>
              {geographies.map(geo => (
                <Geography
                  key={geo.rsmKey}
                  // stroke="#FFF"
                  geography={geo}
                  fill="#DDD"
                />
              ))}
              {locations &&
                locations.map(loc => {
                  if (loc.active) {
                    return (
                      <Marker coordinates={[loc.coordinates.lon, loc.coordinates.lat]} key={loc.id}>
                        <Tooltip label={loc.displayName}>
                          <Box w={16} h={16}>
                            <MdMyLocation />
                          </Box>
                        </Tooltip>
                      </Marker>
                    );
                  }
                })}
            </>
          )}
        </Geographies>
      </ComposableMap>
    </Box>
  );
});
